<?phpclass torpedos extends WP_SMS{	private $wsdl_link = "http://3ss.torpedos.pro/";	public $tariff = "http://3ss.torpedos.pro/";	public $unitrial = true;	public $unit;	public $flash = "enable";	public $isflash = false;	/**	 * torpedos constructor.	 */	public function __construct()	{		parent::__construct();		$this->validateNumber = "55xxxxxxxxxxx";	}	/**	 * @return string|WP_Error	 */	public function SendSMS()	{		// Check gateway credit		if (is_wp_error($this->GetCredit())) {			return new WP_Error('account-credit', __('Your account does not credit for sending sms.', 'wp-sms'));		}		/**		 * Modify sender number		 *		 * @since 3.4		 * @param string $this ->from sender number.		 */		$this->from = apply_filters('wp_sms_from', $this->from);		/**		 * Modify Receiver number		 *		 * @since 3.4		 * @param array $this ->to receiver number		 */		$this->to = apply_filters('wp_sms_to', $this->to);		/**		 * Modify text message		 *		 * @since 3.4		 * @param string $this ->msg text message.		 */		$this->msg = apply_filters('wp_sms_msg', $this->msg);		$this->msg = str_replace(" ", "+", $this->msg);		foreach ($this->to as $to) {			$result = file_get_contents($this->wsdl_link . "index.php?app=ws&u=" . $this->username . "&h=" . $this->password . "&op=pv&to=" . $to . "&msg=" . $this->msg);		}		if ($result) {			$this->InsertToDB($this->from, $this->msg, $this->to);			/**			 * Run hook after send sms.			 *			 * @since 2.4			 * @param string $result result output.			 */			do_action('wp_sms_send', $result);			return $result;		}		return new WP_Error('send-sms', 'Sending error');	}	/**	 * @return int|WP_Error	 */	public function GetCredit()	{		// Check username and password		if (!$this->username && !$this->password) {			return new WP_Error('account-credit', __('Username/Password does not set for this gateway', 'wp-sms'));		}		return 1;	}}